<?phpnamespace Seller\Model;class AdvertisementModel{	public function saveOrUpdate($data)	{		$ins_data = array();		$ins_data['advmname'] = $data['advmname'];		$ins_data['thumbimg'] = save_media($data['thumbimg']);		$ins_data['link'] = $data['link'];		$ins_data['displayorder'] = $data['displayorder'];		$ins_data['enabled'] = $data['enabled'];		$ins_data['addtime'] = time();		$ins_data['linktype'] = $data['linktype'];		$ins_data['appid'] = $data['appid'];				$id = $data['id'];		if( !empty($id) && $id > 0 )		{			unset($ins_data['addtime']);						M('lionfish_comshop_advertisement')->where( array('id' => $id) )->save( $ins_data );						$id = $data['id'];					}else{			$id = M('lionfish_comshop_advertisement')->add( $ins_data );		}	}}?>